<script setup lang="ts">
import { ref } from 'vue';
import axios from 'axios'; // Assuming axios for API calls

const username = ref('');
const password = ref('');

interface UserData {
  username: string;
  password: string;
}

function validateInput(data: UserData): boolean {
  if (!data.username || data.username.length < 3) {
    alert('Username must be at least 3 characters.');
    return false;
  }
  if (!data.password || data.password.length < 6) {
    alert('Password must be at least 6 characters.');
    return false;
  }
  return true;
}

async function submitUserData(data: UserData): Promise<boolean> {
  try {
    const response = await axios.post('/api/register', data);
    console.log('Registration successful:', response.data);
    return true;
  } catch (error) {
    console.error('Registration failed:', error);
    alert('Registration failed. Please try again.');
    return false;
  }
}

async function handleSubmit() {
  const userData: UserData = { username: username.value, password: password.value };
  if (validateInput(userData)) {
    await submitUserData(userData);
  }
}
</script>
