// TypeScript
interface ValidationResult {
  isValid: boolean;
  message?: string;
}

function validateRequired(value: string, fieldName: string): ValidationResult {
  if (!value || value.trim() === '') {
    return { isValid: false, message: `${fieldName} is required.` };
  }
  return { isValid: true };
}

function validateMinLength(value: string, minLength: number, fieldName: string): ValidationResult {
  if (value.length < minLength) {
    return { isValid: false, message: `${fieldName} must be at least ${minLength} characters long.` };
  }
  return { isValid: true };
}

function validateUserName(username: string): ValidationResult {
  let result = validateRequired(username, 'Username');
  if (!result.isValid) return result;

  result = validateMinLength(username, 5, 'Username');
  if (!result.isValid) return result;

  // Specific username validation
  return { isValid: true };
}

function validateEmailAddress(email: string): ValidationResult {
  let result = validateRequired(email, 'Email');
  if (!result.isValid) return result;

  // Specific email format validation (simplified)
  if (!email.includes('@')) {
    return { isValid: false, message: 'Invalid email format.' };
  }

  return { isValid: true };
}

