// TypeScript - Encapsulation for Product
class Product {
  private _name: string;
  private _price: number;
  private _stock: number;

  constructor(name: string, price: number, stock: number) {
    this._name = name;
    this.setPrice(price);   // Use setter for initial validation
    this.setStock(stock);   // Use setter for initial validation
  }

  public getName(): string {
    return this._name;
  }

  public getPrice(): number {
    return this._price;
  }

  public setPrice(newPrice: number): void {
    if (newPrice <= 0) {
      throw new Error("Price must be positive.");
    }
    this._price = newPrice;
  }

  public getStock(): number {
    return this._stock;
  }

  public setStock(newStock: number): void {
    if (newStock < 0) {
      throw new Error("Stock cannot be negative.");
    }
    this._stock = newStock;
  }

  public sell(quantity: number): void {
    if (quantity <= 0) {
      throw new Error("Quantity to sell must be positive.");
    }
    if (this._stock < quantity) {
      throw new Error("Not enough stock to sell.");
    }
    this._stock -= quantity;
    console.log(`${quantity} units of ${this._name} sold. Remaining stock: ${this._stock}`);
  }
}

// Usage:
