// TypeScript - OOP Naming Conventions
interface CartItem {
  productId: string;
  productName: string;
  quantity: number;
  unitPrice: number;
}

class ShoppingCart {
  private items: CartItem[]; // private field, camelCase
  private readonly MAX_ITEMS_IN_CART = 100; // Constant, ALL_CAPS_SNAKE_CASE

  constructor() {
    this.items = [];
  }

  // Method name: camelCase, verb-noun
  public addItemToCart(productId: string, productName: string, quantity: number, unitPrice: number): void {
    if (this.items.length >= this.MAX_ITEMS_IN_CART) {
      console.warn("Cannot add more items, cart is full.");
      return;
    }
    const existingItem = this.items.find(item => item.productId === productId);
    if (existingItem) {
      existingItem.quantity += quantity;
    } else {
      this.items.push({ productId, productName, quantity, unitPrice });
    }
    console.log(`${quantity} of ${productName} added to cart.`);
  }

  // Method name: camelCase, verb-noun
  public removeItemFromCart(productId: string): void {
    this.items = this.items.filter(item => item.productId !== productId);
    console.log(`Item with ID ${productId} removed.`);
  }

  // Method name: camelCase, verb-noun
  public calculateTotalPrice(): number {
    let totalPrice = 0;
    for (const item of this.items) {
      totalPrice += item.quantity * item.unitPrice;
    }
    return totalPrice;
  }

  // Getter method: camelCase, getNoun
  public getCartItems(): CartItem[] {
    return [...this.items]; // Return a copy to prevent external modification
  }
}

// Usage:
